using System;
using System.Diagnostics;
using System.Linq;
using System.Security.Cryptography;
using System.Text;

namespace LeetCode
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine(LongestPalindrome("b"));
            Console.WriteLine(LongestPalindrome("bb"));
            Console.WriteLine(LongestPalindrome("bbb"));
            Console.WriteLine(LongestPalindrome("bbbb"));
            Console.WriteLine(LongestPalindrome(""));
            Console.WriteLine(LongestPalindrome(""));
            Console.WriteLine(LongestPalindrome(""));
            Console.WriteLine(LongestPalindrome(""));
            Console.WriteLine(LongestPalindrome(""));
            Console.WriteLine(LongestPalindrome(""));
            Console.ReadLine();
        }

        static string LongestPalindrome(string s)
        {
            if (string.IsNullOrWhiteSpace(s) || s.Length == 1)
                return s;
            int ret_idx0 = 0;
            int ret_idx1 = s.Length - 1;
            int max_len = 0;
            int center0 = 0;

            //奇数
            while (center0 < s.Length)
            {
                int d = 0;

                while (center0 - d >= 0 && center0 + d <= s.Length - 1 && s[center0 - d] == s[center0 + d])
                {
                    if (2 * d + 1 > max_len)
                    {
                        max_len = 2 * d + 1;
                        ret_idx0 = center0 - d;
                        ret_idx1 = center0 + d;
                    }

                    d++;
                }

                center0++;
            }

            center0 = 0;
            int center1 = center0 + 1;

            //偶数
            while (center1 < s.Length)
            {
                int d = 0;
                while (center0 - d >= 0 && center1 + d <= s.Length - 1 && s[center0 - d] == s[center1 + d])
                {
                    if (2 * d + 2 > max_len)
                    {
                        max_len = 2 * d + 2;
                        ret_idx0 = center0 - d;
                        ret_idx1 = center1 + d;
                    }

                    d++;
                }

                center0++;
                center1++;
            }

            return s.Substring(ret_idx0, ret_idx1 - ret_idx0 + 1);
        }
    }
}
